    showNotify() {
      setTimeout(function () {
        [].forEach.call(document.querySelectorAll('[data-notify="container"]'), function (item) {
          item.classList.add('show')
          setTimeout(function () {
            item.classList.remove('show')
          }, 4000)
          document.querySelectorAll('[data-notify="dismiss"]').onclick = function (event) {
            event.preventDefault()
            item.classList.remove('show')
          }
        })
      }, 500);
    },
    showNotification(type, icon, message, from, align) {
      $.notify({icon: icon, message: message}, {allow_dismiss: false, type: type, z_index: 999999, newest_on_top: true, delay: 150000000, timer: 2500, placement: {from: from, align: align}})
    },
    cancelOrder() {
      let _this = this
      _this.loaded = true
      axios.post(location.origin + '/api/requests/delete', {id: this.order.id, lang: this.data.lang}, this.config).then(response => {
        _this.showNotification('success', 'notifications', response.data.success, 'top', 'center')
        _this.showNotify()
        _this.loaded = false
      })
    },
    onSubmit() {
      let _this = this
      _this.loaded = true
      axios.post(location.origin + '/api/requests/update', {id: this.order.id, data: this.fields, lang: this.data.lang}, this.config).then(response => {
        _this.fields.step = response.data.order.detail.step
        if(response.data.order.detail.step === 4) {
          _this.showNotification('success', 'notifications', response.data.success, 'top', 'center')
          _this.showNotify()
        }
        _this.loaded = false
      }).catch(error => {
        if (typeof error.response !== 'undefined' && error.response.status === 422) {
          let data = error.response.data.errors
          _this.showNotification('danger', 'notifications', data[Object.keys(data)[0]], 'top', 'center')
          _this.showNotify()
          _this.loaded = false
        }
      })

    }
  }
}
